import React from 'react';
import styled from 'styled-components';

// 메인 컨테이너 스타일
const MainContainer = styled.div`
  width: 100%;
  height: 100%;
  padding-left: 340px;
  padding-right: 340px;
  padding-top: 20px;
  padding-bottom: 20px;
  background: #070814;
  justify-content: center;
  align-items: center;
  display: inline-flex;
`;

// 내부 컨테이너 스타일
const InnerContainer = styled.div`
  flex: 1 1 0;
  align-self: stretch;
  justify-content: center;
  align-items: center;
  gap: 708.7px;
  display: inline-flex;
`;

// 로고 컨테이너 스타일
const LogoContainer = styled.div`
  width: 154px;
  align-self: stretch;
  padding-bottom: 3px;
  justify-content: center;
  align-items: center;
  display: inline-flex;
`;

// 메뉴 아이템 컨테이너 스타일
const MenuItemContainer = styled.div`
  width: 377.3px;
  align-self: stretch;
  justify-content: center;
  align-items: center;
  gap: 32px;
  display: inline-flex;
`;

// 메뉴 아이템 스타일
const MenuItem = styled.div`
  width: 48.75px; /* 이 값은 각 메뉴 아이템에 따라 조정될 수 있습니다. */
  height: 25.19px;
  padding-top: 2px;
  padding-bottom: 2.19px;
  justify-content: center;
  align-items: center;
  display: inline-flex;
`;

// 메뉴 텍스트 스타일
const MenuText = styled.div`
  width: 61.91px; /* 이 값은 각 텍스트에 따라 조정될 수 있습니다. */
  height: 21px;
  color: white;
  font-size: 18px;
  font-family: 'Pretendard';
  font-weight: 500;
  line-height: 25.20px;
  word-wrap: break-word;
  text-align: center; /* 중앙 정렬을 위해 추가 */
`;

// "지원하기" 버튼 스타일
const ApplyButton = styled(MenuItem)`
  flex: 1 1 0;
  height: 42px;
  padding-top: 10.41px;
  padding-bottom: 10.59px;
  padding-left: 24px;
  padding-right: 23.61px;
  background: #343744;
`;

// 컴포넌트 정의
const Header = () => {
  return (
    <MainContainer>
      <InnerContainer>
        <LogoContainer>
          <img style={{ width: 154, height: 18.89, position: 'relative' }} src="https://via.placeholder.com/154x19" alt="Logo" />
        </LogoContainer>
        <MenuItemContainer>
          <MenuItem><MenuText>About</MenuText></MenuItem>
          <MenuItem><MenuText>모집안내</MenuText></MenuItem>
          <MenuItem><MenuText>프로젝트</MenuText></MenuItem>
          <ApplyButton><MenuText>지원하기</MenuText></ApplyButton>
        </MenuItemContainer>
      </InnerContainer>
    </MainContainer>
  );
};

export default Header;
