// src/components/footer/Footer.jsx
import React from 'react';
import styled from 'styled-components';


const FooterContainer = styled.div`
  font-size: 0.8rem;
  //font-weight: bold;
  @media screen and (max-width: 600px) {
    font-size: 1vw;
  }
`;

//공지사항 
const NoticeContainer = styled.div`
  background: #ffffff;
  border-top: solid 1px #d9d9d9;
  border-bottom: solid 1px #d9d9d9;
  color: #838383;

  padding-left: 10vw;
  height: 3vw;
  display: flex;
  align-items: center;
  @media screen and (max-width: 600px) {
    font-size: 1vw;
  }
`;

//하단 전체
const FooterInfoContainer = styled.div`
  background: #fbfafa;
  padding-left: 10vw;


  display: flex;
  flex-direction: column;
  justify-items: center;
  @media screen and (max-width: 600px) {
    font-size: 1vw;
  }
`;

//회사소개...., 서비스 이용약관.. 라인 (추후 링크 걸 수 있는 라인)
const FooterInfoBtnContainer = styled.li`
  display: flex;
  list-style: none;
  margin: 1vw 0;

  li {
    margin-right: 2vw;
  }
  @media screen and (max-width: 600px) {
    font-size: 1vw;
  }
`;


const FooterContactContainer = styled.footer`
  color: #838383;
  margin-bottom: 1vw;
  line-height: 1.5;

  a {
    color: inherit;
    text-decoration: none;
  }

  span {
    margin-right: 0.5vw;
  }

  button {
    margin-top: 1vw;
    margin-right: 1vw;
    margin-bottom: 1vw;
    padding: 0.5vw 0.7vw;

    color: #582fff;
    //font-size: 0.7rem;
    font-weight: bold;

    border: none;
    background: #dad1fb;
    border-radius: 0.3vw;
  }

  @media screen and (max-width: 600px) {
    button {
      margin-top: 1vw;
      margin-right: 1vw;
      margin-bottom: 1vw;
      padding: 0.5vw 1vw;
      font-size: 1vw;
    }
  }
`;

const FooterCompanyInfoContainer = styled.div`
  margin-right: 30vw;
  line-height: 1.5;

  @media screen and (max-width: 600px) {
    font-size: 1vw;
  }
`;

const Footer = () => {
    return (
        <FooterContainer>
            <NoticeContainer>
                <span>공지사항</span><span style={{marginLeft: "2vw"}}>유료 행사 참가자 신청 프로세스 변경 안내</span>
            </NoticeContainer>
            <FooterCompanyInfoContainer>
                <br></br>
                <div></div>
                <div style={{fontSize:"0.7vw", color: "#838383", marginBottom: "1vw"}}>Copyright HIVe.   All Rights Reserved</div>
            </FooterCompanyInfoContainer>
        </FooterContainer>
    );
};

export default Footer;
