// React 및 관련 라이브러리 import
import React, { useState, useEffect } from "react";
import styled from "styled-components";

// 배너 이미지 import
import Banner1 from "../../assets/images/banner1.png";

// 상단-배너+인기검색어 스타일 정의
const TopContainer = styled.div`

  display: flex;
  flex-direction: column;
  background-color: #2A2A2A;
`;

const BannerAndPopular = styled.div`
  display: flex;
  gap: 3vw;
  margin: 0 auto;
`;

const HomeP = styled.p`
  color: #fff;
  font-size: 1.3vw;
  font-weight: bold;
  padding: 0.5vw 0 0 17vw;
`;

const PickText = styled.span`
  color: #582fff;
  font-weight: bold;
`;

const TextP = styled.span`
  color: #000000;
  font-weight: bold;
`;

// 배너 스타일 정의
const BannerB = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;

`;

const BannerContainer = styled.div`
  display: flex;
  justify-content: center;
  text-align: center;
  align-items: center;
  gap:1vw;
  padding: 0 0 2vw 0;
  margin-left:-4vw;
`;

const Slides = styled.div`
  width: 100%;
  height: 100%;
`;


const Image = styled.img`
  width: 24.25vw;
  height: 13.27vw;
  //border-radius: 0.5vw;
`;

const Button = styled.img`
  width: 2.5vw;
  height: 2.5vw;
  cursor: pointer;
`;

const Banner = () => {

  // 배너 이미지 배열
  const images = [Banner1, Banner1, Banner1];
  const [currentSlide, setCurrentSlide] = useState(0);
  // 슬라이드 변경 처리 함수
  const handleSlideChange = (direction) => {
    let newIndex = currentSlide + direction;

    if (newIndex < 0) {
      newIndex = images.length - 1;
    } else if (newIndex >= images.length) {
      newIndex = 0;
    }

    setCurrentSlide(newIndex);
  };

  // 자동 슬라이드 타이머 설정
  useEffect(() => {
    const autoSlideTimer = setInterval(() => {
      setCurrentSlide((prevSlide) => {
        let newIndex = prevSlide + 1;
        if (newIndex >= images.length) {
          newIndex = 0;
        }
        return newIndex;
      });
    }, 3000);

    // 컴포넌트 언마운트 시 타이머 정리
    return () => {
      clearInterval(autoSlideTimer);
    };
  }, [images.length]);
  //인기검색어
  const popularKeywords = [
    "경영 세미나",
    "취업",
    "학술대회",
    "대학 경연",
    "대학 세미나",
    "박람회",
  ];

  return (
    <TopContainer>
      <HomeP>
        HIVe <PickText>활동</PickText>
      </HomeP>
      <BannerAndPopular>

      </BannerAndPopular>
    </TopContainer>
  )
}


export default Banner;
