import React from 'react';
import styled from 'styled-components';

// 미디어 쿼리를 위한 기본 설정
const sizes = {
  mobile: '320px',
  tablet: '768px',
  desktop: '1024px',
};

// 미디어 쿼리 헬퍼 함수
const media = {
  mobile: `(min-width: ${sizes.mobile})`,
  tablet: `(min-width: ${sizes.tablet})`,
  desktop: `(min-width: ${sizes.desktop})`,
};

// 메인 컨테이너 스타일 (반응형)
const MainContainer = styled.div`
  width: 100%;
  height: 100%;
  padding: 20px;
  background: #070814;
  justify-content: center;
  align-items: center;
  display: inline-flex;
  flex-wrap: wrap; // 내용이 넘치면 다음 줄로

  @media ${media.tablet} {
    padding-left: 10%;
    padding-right: 10%;
  }

  @media ${media.desktop} {
    padding-left: 340px;
    padding-right: 340px;
  }
`;

// 로고 및 메뉴 아이템 컨테이너 (간소화된 스타일링)
const ItemContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px; // 기본 간격
  flex-grow: 1;

  @media ${media.mobile} {
    flex-direction: column;
  }

  @media ${media.tablet} {
    flex-direction: row;
    gap: 50px;
  }
`;

// 로고, 메뉴 텍스트, "지원하기" 버튼 스타일 (간소화 및 반응형)
const Text = styled.div`
  color: white;
  font-size: 16px; // 기본 폰트 크기
  text-align: center;

  @media ${media.tablet} {
    font-size: 18px;
  }
`;

// 컴포넌트 정의
const Header = () => {
  return (
    <MainContainer>
      <ItemContainer>
        <img src="https://via.placeholder.com/154x19" alt="Logo" style={{ maxWidth: '100%', height: 'auto' }} />
      </ItemContainer>
      <ItemContainer>
        <Text>About</Text>
        <Text>모집안내</Text>
        <Text>프로젝트</Text>
        <Text style={{ background: '#343744', padding: '10px 20px', borderRadius: '5px' }}>지원하기</Text>
      </ItemContainer>
    </MainContainer>
  );
};

export default Header;
