import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import search from "../../assets/images/search.png";
import setting from "../../assets/images/setting.png";


// 헤더 컨테이너 스타일
const HeaderContainer = styled.div`
  width: 100%;
  height: 100%;

  box-shadow: 0 0.3vw 0.3vw rgba(0, 0, 0, 0.25);
  margin: 2vw 0 0.5vw 0;
`;

// 로그인과 회원가입을 포함하는 컨테이너 스타일
const AuthButtonsContainer = styled.div`
  align-items: center;
`;

// 로고와 검색창을 포함한 컨테이너 스타일
const LogoSearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

// 로고 이미지 스타일
const LogoImage = styled.img`
  width: 20vw;
  cursor: pointer;
  margin-bottom: 1vw;
  padding: 1vw;
`;

// 검색 아이콘 스타일
const SearchImage = styled.img`
  width: 1vw;
  height: 1vw;
  margin-left: 1vw;
  position: absolute;
`;

// 검색 아이콘과 검색 입력창을 포함하는 컨테이너 스타일
const SearchItem = styled.div`
  width: 40%;
  display: flex;
  align-items: center;
  color: #582fff;
`;

// 검색 입력창 스타일
const SearchInput = styled.input`
  width: 50%;
  border: 0.2vw solid transparent;
  border-image: linear-gradient(45deg, #582fff, #0085ff) 1;
  border-radius: 1vw;
  padding: 0.7vw 1vw 0.7vw 2.5vw;
  font-size: 0.8vw;
  outline: none;
`;

// 검색 버튼 스타일
const SearchButton = styled.button`
  background-color: #582fff;
  color: #ffffff;
  border: none #582fff;
  border-radius: 0.5vw;
  padding: 0.7vw 1vw;
  font-size: 0.8vw;
  cursor: pointer;
  margin-left: 1vw;

  &:hover {
    background-color: #838383;
  }
`;

// 로그인 버튼 스타일
const LoginButtonStyle = styled.button`
  white-space: nowrap;
  background-color: #ffffff;
  color: #838383;
  font-weight: bold;
  font-size: 0.8rem;
  border: none;

  &:hover {
    text-decoration: underline;
  }
`;
// 회원가입 버튼 스타일
const Join = styled(Link)`
  background-color: #ffffff;
  color: #838383;
  font-weight: bold;
  padding: 1vw 1vw 1vw 1vw;
  font-size: 0.8rem;
  border: none;
  text-decoration: none; // 밑줄 없애는 스타일

  &:hover {
    text-decoration: underline;
  }
`;
// 환영 메시지 스타일
const WelcomeText = styled.span`
  color: #582fff;
  font-size: 0.8rem;
  margin-left: 1vw;
`;


// 환경설정 아이콘 스타일
const SettingImage = styled.img`
  width: 100%;
  height: 100%;
`;

// 링크 목록을 포함하는 컨테이너 스타일
const LinkContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.7vw;
  flex-wrap: wrap;
  @media (max-width: 950px) {
    font-size: 0.8vw;
    gap: 5vw;
  }

  @media (max-width: 500px) {
    font-size: 0.8vw;
    gap: 1vw;
  }
`;

// 각각의 링크 아이템 스타일
const LinkItem = styled(Link)`
  color: #838383;
  text-decoration: none;
  font-weight: bold;
  margin-left: 5vw;
  font-size: 0.8rem;
  transition: color 0.3s; /* 색상 변화에 대한 트랜지션 효과 추가 */

  &:hover {
    color: #582fff;
  }
`;

// 행사등록하기 링크 스타일
const RegisterItem = styled(Link)`
  color: #582fff;
  text-decoration: none;
  font-weight: bold;
  font-size: 0.8rem;
  margin-left: 23vw;
  margin-right: 1vw;
  border: none;

  &:hover {
    color: #838383;
  }
`;

// 라인 1
const Line = styled.div`
  margin-top: -1vw;
  width: 100%;
  height: 0.1vw;
  background-color: #d9d9d9;
`;

const VerticalLine = styled.div`
  width: 0.1vw;
  height: 1.5vw;
  background-color: #d9d9d9;
`;

const Header = () => {
    // 검색어
    const [searchTerm, setSearchTerm] = useState("");

    // 검색어 변경 핸들러
    const handleSearchTermChange = (event) => {
        setSearchTerm(event.target.value);
    };

    // 검색 버튼 클릭 핸들러
    const handleSearchButtonClick = () => {
        // 여기에서 검색 버튼이 클릭되었을 때 수행할 동작을 추가
        // 예를 들면 검색 결과를 가져오거나 페이지를 이동하는 등의 동작
        console.log("검색어:", searchTerm);
    };

    // 로그인
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    // 로그인 버튼 클릭 핸들러
    const handleButtonClick = () => {
        setIsLoggedIn(!isLoggedIn);
    };

    return (
        <HeaderContainer>
            <LogoSearchContainer>
                <Link to="/">

                </Link>
                <AuthButtonsContainer>
                    <Link to="/login">
                        <LoginButtonStyle onClick={handleButtonClick}>
                            {isLoggedIn ? "로그아웃" : "로그인"}
                        </LoginButtonStyle>
                    </Link>
                    <SearchItem>
                        <SearchImage src={search} alt="search" />
                        <SearchInput
                            onChange={handleSearchTermChange}
                            value={searchTerm}
                            placeholder="2024년도 취업 세미나"
                        />
                        <SearchButton onClick={handleSearchButtonClick}>검색</SearchButton>
                    </SearchItem>
                    <Join to="/join">
                        {isLoggedIn ? (
                            <SettingImage src={setting} alt="setting" />
                        ) : (
                            "회원가입"
                        )}
                    </Join>
                </AuthButtonsContainer>
            </LogoSearchContainer>
            <Line />
            <LinkContainer>
                <LinkItem to="/festival-list">행사목록</LinkItem>
                <LinkItem to="/diary">기록장</LinkItem>
                <LinkItem to="/community">커뮤니티</LinkItem>
                <LinkItem to="/mypage">마이페이지</LinkItem>
                <RegisterItem to="/register">행사등록하기</RegisterItem>
                <VerticalLine />
                <WelcomeText>
                    {isLoggedIn ? "000님 환영합니다!" : "로그인 해주세요!"}
                </WelcomeText>
            </LinkContainer>
        </HeaderContainer>
    );
};

export default Header;
