import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";


// 헤더 컨테이너 스타일
const HeaderContainer = styled.div`
display: flex;
width: 1920px;
height: 82px;
padding: 20px 340px;
justify-content: center;
align-items: center;
flex-shrink: 0;
background: #070814;
`;

const LinkContainer = styled.div`
display: flex;
width: 1240px;
height: 42px;
justify-content: center;
align-items: center;
gap: 708.7px;
flex-shrink: 0;
`;

const LinkItem = styled(Link)`
  color: #ffffff;
  text-decoration: none;
  font-weight: bold;
  margin-left: 5vx;
  font-size: 1.5rem;
  transition: color 0.3s;

  &: hover {
    color: #5365FF;
  }
`;

const Header = () => {

  return (
    <HeaderContainer>
      <LinkContainer>
        <LinkItem to="/about">About</LinkItem>
        <LinkItem to="/recruit">모집안내</LinkItem>
        <LinkItem to="/project">프로젝트</LinkItem>
      </LinkContainer>
    </HeaderContainer>
  );
};

export default Header;
