import React, { useState } from "react";
import { Link } from "react-router-dom";
import logo from "../../assets/images/logo.png"
import styled from "styled-components";


// 헤더 컨테이너 스타일
const HeaderContainer = styled.div`
  display: flex;
  width: 100%;
  height: 82px;
  justify-content: center;
  align-center: center;
  flex-shrink: 0;
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LogoImage = styled.img`
  width: 20vw;
  cursor: pointer;
  margin-bottom: 1vw;
  padding: 1vw;
`;

const LinkContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.7vw;
  flex-wrap: wrap;
  @media (max-width: 950px){
    font-size: 0.8vw;
    gap: 5vw;
  }

  @media (max-width: 500px) {
    font-size: 0.8vw;
    gap: 1vw;
  }
`;

const LinkItem = styled(Link)`
  color: #ffffff;
  text-decoration: none;
  font-weight: bold;
  margin-left: 5vx;
  font-size: 1.5rem;
  transition: color 0.3s;

  &: hover {
    color: #5365FF;
  }
`;

const Header = () => {

  return (
    <HeaderContainer>
      <LogoContainer>
        <Link to="/">
          <LogoImage src={logo} alt="logo"></LogoImage>
        </Link>
      </LogoContainer>
      <LinkContainer>
        <LinkItem to="/about">About</LinkItem>
        <LinkItem to="/recruit">모집안내</LinkItem>
        <LinkItem to="/project">프로젝트</LinkItem>
      </LinkContainer>
    </HeaderContainer>
  );
};

export default Header;
