import React from "react";
import styled from "styled-components";

// 미디어 쿼리를 위한 기본 설정
const sizes = {
  tablet: '768px',
  desktop: '1024px',
};

const media = {
  tablet: `(max-width: ${sizes.tablet})`,
  desktop: `(min-width: ${sizes.desktop})`,
};

const StyledFooter = styled.footer`
  background-color: #070814;
  padding: 20px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;

  @media ${media.desktop} {
    height: 218px;
    width: 100%;
  }
`;

const FooterContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 20px;

  @media ${media.desktop} {
    flex-direction: column;
    justify-content: space-around;
    width: 1240px;
  }
`;

const SocialLinks = styled.div`
  display: flex;
  gap: 20px;
`;

const SocialLink = styled.a`
  text-decoration: none;
  color: #a5b2c5;
  font-family: "Pretendard-Medium", Helvetica, sans-serif;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: -0.2px;
`;

const FooterInfo = styled.div`
  color: #606475;
  font-family: "Pretendard-Medium", Helvetica, sans-serif;
  font-size: 18px;
  font-weight: 500;
`;

export const Footer = () => {
  return (
    <StyledFooter>
      <FooterContent>
        <SocialLinks>
          <SocialLink href="#">Hongik Univ.</SocialLink>
          <SocialLink href="#">Introduce</SocialLink>
          <SocialLink href="#">Instagram</SocialLink>
          </SocialLinks>
          <SocialLink>
          <SocialLink href="#">Kakao channel    |    @HIVe</SocialLink>
          <SocialLink href="#">E-mail   |   hive_hongik@naver.com</SocialLink>
          </SocialLink>
        <FooterInfo>© 2024 HIVe. ALL RIGHTS RESERVED.</FooterInfo>
      </FooterContent>
    </StyledFooter>
  );
};

export default Footer;
