import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import search from "../../assets/images/search.png";
import setting from "../../assets/images/setting.png";


// 헤더 컨테이너 스타일
const HeaderContainer = styled.div`
  width: 100%;
  height: 100%;
  background: #000;

  color: #fff;
  box-shadow: 0 0.3vw 0.3vw rgba(0, 0, 0, 0.25);
  margin: 2vw 0 0.5vw 0;
`;

// 로그인과 회원가입을 포함하는 컨테이너 스타일
const AuthButtonsContainer = styled.div`
  align-items: center;
`;

// 로고와 검색창을 포함한 컨테이너 스타일
const LogoSearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

// 로고 이미지 스타일
const LogoImage = styled.img`
  width: 20vw;
  cursor: pointer;
  margin-bottom: 1vw;
  padding: 1vw;
`;

// 라인 1
const Line = styled.div`
  margin-top: -1vw;
  width: 100%;
  height: 0.1vw;
  background-color: #d9d9d9;
`;

const VerticalLine = styled.div`
  width: 0.1vw;
  height: 1.5vw;
  background-color: #d9d9d9;
`;

const Header = () => {
    // 검색어
    const [searchTerm, setSearchTerm] = useState("");

    // 검색어 변경 핸들러
    const handleSearchTermChange = (event) => {
        setSearchTerm(event.target.value);
    };

    // 검색 버튼 클릭 핸들러
    const handleSearchButtonClick = () => {
        // 여기에서 검색 버튼이 클릭되었을 때 수행할 동작을 추가
        // 예를 들면 검색 결과를 가져오거나 페이지를 이동하는 등의 동작
        console.log("검색어:", searchTerm);
    };

    // 로그인
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    // 로그인 버튼 클릭 핸들러
    const handleButtonClick = () => {
        setIsLoggedIn(!isLoggedIn);
    };

    return (
        <HeaderContainer>
            <LogoSearchContainer>
                <Link to="/">

                </Link>
                </LogoSearchContainer>
            <Line />
        </HeaderContainer>
    );
};

export default Header;
